package com.mycompany.firstJava.ch02;

public class Ex_int {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int var1 = 10; // 10진수
		int var2 = 012; // 8진수
		int var3 = 0xa; // 16진수

		
		System.out.println("var1:" + var1);
		System.out.println("var2:" + var2);
		System.out.println("var3:" + var3);
		
	}

}
