package com.mycompany.firstJava.ch04;

public class Ex_08_for {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 중첩 for문
		
		// 2x부터 출력되는 구구단 프로그램을 작성하시오.
		// 각 단계 마다 구분선을 넣어 주세요.
		// 2*1 = 2
		
		
		for (int i=2; i<=9; i++) {
				for(int j=1; j<=9; j++) {
					System.out.println(i + "*" + j + "=" + i*j);
			}
			System.out.println("------------------------------");
			

			
		}
			
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
