package com.mycompany.firstJava.ch04;

public class Ex_09_While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		// while문
		
//		1부터 10까지 더하는 프로그램 작성
		
		int i=1;
		int sum = 0;
		
		
		while ( i<=10 ) {
			sum = sum+i;
			i++;
		//	if(i == 11) break;
			System.out.println(sum);	
		}
		
		
		System.out.println("sum: " + sum);
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
